frappe.ui.form.on('Delivery Note', {
    refresh: function (frm) {
        if (frm.doc.docstatus === 0 && !frm.doc.__islocal) {
            // Check stock status on load for existing drafts
            frm.trigger('check_stock_availability');
        }
    },

    validate: function (frm) {
        if (frm.doc.docstatus === 0) {
            frm.trigger('check_stock_availability');
        }
    },

    check_stock_availability: function (frm) {
        frappe.call({
            method: "systech.services.workflow.validate_dn_stock",
            args: {
                docname: frm.doc.name
            },
            callback: function (r) {
                if (r.message && r.message.status === "failed") {
                    // Show warning / blocked status
                    handle_insufficient_stock(frm, r.message);
                } else {
                    // Clear any previous indicators if possible
                    frm.dashboard.clear_headline();
                }
            }
        });
    }
});

function handle_insufficient_stock(frm, data) {
    let msg = __("Insufficient Actual Stock. Some items are reserved by other Approved Orders.");

    // Show headline
    frm.dashboard.set_headline_alert(
        `<div class="row">
            <div class="col-xs-12">
                <span class="indicator red">${msg}</span>
            </div>
        </div>`
    );

    // Show Dialog with conflict details
    let items_html = data.items.map(i => `<li><b>${i.item_code}</b>: Req ${i.required}, Available ${i.available}</li>`).join("");

    let conflict_html = "";
    if (data.blockers && data.blockers.length > 0) {
        conflict_html = "<b>Conflicting Orders:</b><br><ul>";
        data.blockers.forEach(b => {
            conflict_html += `<li><a href="/app/sales-order/${b.name}">${b.name}</a> (${b.customer}) - Qty: ${b.qty}</li>`;
        });
        conflict_html += "</ul>";
    }

    frappe.msgprint({
        title: __('Stock Unavailable'),
        message: `<ul>${items_html}</ul><hr>${conflict_html}`,
        indicator: 'red'
    });

    // Add Request Release Button
    frm.add_custom_button(__('Request Stock Release'), function () {
        request_release_from_blockers(frm, data.blockers);
    }).addClass('btn-danger');
}

function request_release_from_blockers(frm, blockers) {
    if (!blockers || blockers.length === 0) {
        frappe.msgprint("No specific conflicting orders found to release from.");
        return;
    }

    let d = new frappe.ui.Dialog({
        title: 'Request Stock Release',
        fields: [
            {
                label: 'Select Order to Release From',
                fieldname: 'source_order',
                fieldtype: 'Select',
                options: blockers.map(b => b.name),
                reqd: 1
            }
        ],
        primary_action_label: 'Send Request',
        primary_action: function (values) {
            frappe.call({
                method: "systech.services.workflow.request_release",
                args: {
                    docname: values.source_order, // The APPROVED order holding stock
                    source_docname: frm.doc.name // THIS Delivery Note asking for it
                },
                callback: function (r) {
                    d.hide();
                    frappe.msgprint("Release Request Sent to Managers");
                }
            });
        }
    });

    d.show();
}

/* Purchase Receipt Security Script */
/* Hides prices in FORM and LIST VIEW for Warehouse Keeper */

// 1. Helper function
function is_warehouse_keeper_only() {
    const excluded_roles = ["System Manager", "Accounts Manager", "Purchase Manager"];
    return frappe.user.has_role("Warehouse Keeper") 
        && !frappe.user_roles.some(r => excluded_roles.includes(r));
}

// 2. Form Logic (unchanged from before)
function hide_prices_for_warehouse_keeper(frm) {
    if (!is_warehouse_keeper_only()) return;

    const hidden_fields = [
        'base_discount_amount', 'base_grand_total', 'base_net_total',
        'base_total', 'base_total_taxes_and_charges', 'discount_amount',
        'grand_total', 'net_total', 'total', 'total_taxes_and_charges',
        'taxes', 'buying_price_list', 'price_list_currency',
        'plc_conversion_rate', 'currency', 'conversion_rate',
        'total_qty', 'total_net_weight', 'rounding_adjustment',
        'rounded_total', 'in_words',
        'rate', 'amount', 'base_rate', 'base_amount',
        'net_rate', 'net_amount', 'valuation_rate',
        'price_list_rate', 'discount_percentage',
        'rm_supp_cost', 'landed_cost_voucher_amount',
        'margin_type', 'margin_rate_or_amount',
        'rate_with_margin', 'billed_amt',
        'amount_difference_with_purchase_invoice'
    ];

    hidden_fields.forEach(f => frm.set_df_property(f, 'hidden', 1));

    const selectors = hidden_fields.map(f => `
        div[data-fieldname="${f}"],
        .form-group[data-fieldname="${f}"],
        .grid-row-cell[data-fieldname="${f}"],
        .grid-static-col[data-fieldname="${f}"],
        .grid-heading-row [data-fieldname="${f}"],
        .grid-row-open [data-fieldname="${f}"],
        .slide-sheet [data-fieldname="${f}"],
        td[data-fieldname="${f}"],
        th[data-fieldname="${f}"],
        .grid-body [data-fieldname="${f}"],
        .grid-footer [data-fieldname="${f}"],
        .ellipsis[data-fieldname="${f}"],
        span[data-fieldname="${f}"]
    `);

    // Also explicitly force hide list view cells if they have fieldname attributes (v15+)
    selectors.push(`.list-row-col[data-fieldname="grand_total"]`);
    selectors.push(`.list-subject .level-item[data-fieldname="grand_total"]`);

    const css = selectors.join(",") + ` { display: none !important; visibility: hidden !important; } `;
    const style_id = 'warehouse-keeper-security';
    $(`#${style_id}`).remove();
    $('<style>').attr('id', style_id).prop('type', 'text/css').html(css).appendTo('head');
}

frappe.ui.form.on('Purchase Receipt', {
    setup: function(frm) { hide_prices_for_warehouse_keeper(frm); },
    onload: function(frm) { hide_prices_for_warehouse_keeper(frm); },
    refresh: function(frm) { hide_prices_for_warehouse_keeper(frm); },
    validate: function(frm) { hide_prices_for_warehouse_keeper(frm); }
});

// 3. List View Logic - AGGRESSIVE HIDING
frappe.listview_settings['Purchase Receipt'] = {
    onload: function(listview) {
        if (is_warehouse_keeper_only()) {
            // Remove "Grand Total" from the internal columns list so it doesn't render
            listview.settings.add_fields = listview.settings.add_fields || [];
            // We can't easily remove fields once fetched, but we can hide the column by index
        }
    },
    refresh: function(listview) {
        if (is_warehouse_keeper_only()) {
             // Method A: Hide by Column Header Text (Most reliable for "Grand Total")
             // Find the index of the column "Grand Total"

             // Hide Header
             $('.list-row-head .list-col').each(function(index) {
                 if ($(this).text().trim() === "Grand Total") {
                     // Hide this header
                     $(this).css('display', 'none');
                     // Hide all corresponding data cells
                     $(`.list-row .list-col:nth-child(${index + 1})`).css('display', 'none');
                 }
             });

             // Method B: CSS Hammer (Backup)
             // Hides usually the 4th/5th column or right-aligned currency columns
             const css = `
                /* Hide any column with "Grand Total" title attribute */
                .list-col[title="Grand Total"] { display: none !important; }

                /* Hide 4th column specifically (based on your screenshot) */
                /* Adjust nth-child number if it hides the wrong thing! */
                /* .list-row .list-col:nth-child(5), .list-row-head .list-col:nth-child(5) { display: none !important; } */
             `;
              $('<style>').prop('type', 'text/css').html(css).appendTo('head');
        }
    }
};

/* Stock Entry Security */
frappe.ui.form.on('Stock Entry', {
    refresh: function(frm) {

        // Only hide for Warehouse Keeper, NOT Admin
        if (frappe.user.has_role("Warehouse Keeper") && !frappe.user.has_role("Administrator")) {

            // Main Fields
            ['total_amount', 'total_additional_costs', 'total_outgoing_value', 
             'total_incoming_value', 'value_difference', 'difference_amount']
            .forEach(f => frm.set_df_property(f, 'hidden', 1));

            // Grid Columns
            if(frm.fields_dict['items'] && frm.fields_dict['items'].grid) {
                const grid = frm.fields_dict['items'].grid;
                ['basic_rate', 'basic_amount', 'amount', 'valuation_rate', 'additional_cost']
                .forEach(f => {
                    grid.update_docfield_property(f, 'hidden', 1);
                    grid.update_docfield_property(f, 'print_hide', 1);

                    // Only hide in current grid rows, scoped properly
                    grid.wrapper.find(`.grid-row [data-fieldname="${f}"]`).hide();
                });
                grid.refresh();
            }
        }
    }
});

// Child Form View
frappe.ui.form.on('Stock Entry Detail', {
    form_render: function(frm, cdt, cdn) {

        // Only hide for Warehouse Keeper, NOT Admin
        if (frappe.user.has_role("Warehouse Keeper") && !frappe.user.has_role("Administrator")) {
            const fields = ['basic_rate', 'basic_amount', 'amount', 'valuation_rate', 'additional_cost'];

            // Get the child doc row wrapper
            const row = frappe.get_doc(cdt, cdn);
            fields.forEach(f => {
                const $el = frm.fields_dict['items'].grid.get_row(cdn).$row.find(`[data-fieldname="${f}"]`);
                $el.hide();
            });
        }
    }
});

/* Item Security */
frappe.ui.form.on('Item', {
    refresh: function(frm) {
        if (frappe.user.has_role("Warehouse Keeper")) {
            ['valuation_rate', 'standard_rate', 'last_purchase_rate', 'opening_stock_value']
            .forEach(f => frm.set_df_property(f, 'hidden', 1));
        }
    }
});

/* Delivery Note Security Script */
/* Hides price fields for Warehouse Keeper and Sales User */

// Helper to check if user should have prices hidden
function should_hide_prices() {
    const restricted_roles = ["Warehouse Keeper", "Sales User"];
    const excluded_roles = ["System Manager", "Accounts Manager", "Sales Manager"];

    // Check if user has ANY of the restricted roles
    const has_restricted_role = restricted_roles.some(role => frappe.user.has_role(role));

    // Check if user has ANY of the excluded roles
    const has_excluded_role = excluded_roles.some(role => frappe.user.has_role(role));

    return has_restricted_role && !has_excluded_role;
}

function hide_prices_dn(frm) {
    if (!should_hide_prices()) return;

    const hidden_fields = [
        // Main Totals
        'base_grand_total', 'base_net_total', 'base_total_taxes_and_charges',
        'grand_total', 'net_total', 'total_taxes_and_charges', 'taxes',
        'discount_amount', 'rounding_adjustment', 'rounded_total', 'in_words',
        'currency', 'conversion_rate', 'price_list_currency', 'plc_conversion_rate',
        'selling_price_list', 'total_commission', 'margin_type', 'base_total',

        // Child Table (Delivery Note Item)
        'rate', 'amount', 'base_rate', 'base_amount', 
        'net_rate', 'net_amount', 'base_net_rate', 'base_net_amount',
        'price_list_rate', 'discount_percentage', 'discount_amount',
        'margin_rate_or_amount', 'rate_with_margin', 'gross_profit',
        'incoming_rate', 'outgoing_rate'
    ];

    // 1. Standard Hide
    hidden_fields.forEach(f => {
        frm.set_df_property(f, 'hidden', 1);
    });

    // 2. CSS Hide (Grid & Dialogs)
    const selectors = hidden_fields.map(f => `
        div[data-fieldname="${f}"],
        .form-group[data-fieldname="${f}"],
        .grid-row-cell[data-fieldname="${f}"],
        .grid-static-col[data-fieldname="${f}"],
        .grid-heading-row [data-fieldname="${f}"],
        .grid-row-open [data-fieldname="${f}"], 
        .slide-sheet [data-fieldname="${f}"],
        td[data-fieldname="${f}"],
        th[data-fieldname="${f}"],
        .grid-body [data-fieldname="${f}"],
        .grid-footer [data-fieldname="${f}"]
    `);

    const css = selectors.join(", ") + " { display: none !important; }";

    const style_id = 'pro-dn-security';
    $(`#${style_id}`).remove();
    $('<style>').attr('id', style_id).prop('type', 'text/css').html(css).appendTo('head');
}

frappe.ui.form.on('Delivery Note', {
    setup: function(frm) { hide_prices_dn(frm); },
    onload: function(frm) { hide_prices_dn(frm); },
    refresh: function(frm) { hide_prices_dn(frm); },
    validate: function(frm) { hide_prices_dn(frm); }
});

frappe.ui.form.on('Sales Order', {
    refresh: function (frm) {
        // Add custom Email button for submitted Sales Orders
        if (frm.doc.docstatus === 1 && frm.doc.customer) {
            frm.add_custom_button(__('Email'), function () {
                let party_name = frm.doc.customer;

                // Fetch customer email
                frappe.call({
                    method: 'systech.api.email.get_party_email',
                    args: {
                        party_type: 'Customer',
                        party_name: party_name
                    },
                    callback: function (r) {
                        if (r.message) {
                            // Email found, open composer
                            new frappe.views.CommunicationComposer({
                                doc: frm.doc,
                                frm: frm,
                                subject: __('Sales Order: {0}', [frm.doc.name]),
                                recipients: r.message,
                                attach_document_print: true,
                                real_name: party_name
                            });
                        } else {
                            // No email found
                            frappe.msgprint({
                                title: __('Email Not Found'),
                                indicator: 'orange',
                                message: __(
                                    'The Customer <b>{0}</b> does not have an email address.<br><br>' +
                                    'Please <a href="/app/customer/{1}">add email to Customer record</a> first.',
                                    [party_name, party_name]
                                )
                            });
                        }
                    }
                });
            }, __('Actions'));
        }
    }
});

frappe.ui.form.on('Sales Person', {
    refresh: function(frm) {
         if (!frm.doc.__islocal) {
             // 1. Hide Target Details for standard Sales Users
             if (frappe.user.has_role('Sales User') && 
                !frappe.user.has_role('Sales Manager') && 
                !frappe.user.has_role('System Manager') &&
                !frappe.user.has_role('Administrator')) {

                 frm.set_df_property('target_details_section_break', 'hidden', 1);
                 frm.set_df_property('targets', 'hidden', 1);
             }

             // 2. Add Refresh Button
             frm.add_custom_button(__('Refresh Stats'), function() {
                 load_sales_stats(frm);
             }).addClass('btn-primary');

             // 3. Initial Load
             load_sales_stats(frm);
         }
    }
});

function load_sales_stats(frm) {
    frm.set_value('target_status', 'Calculating...');

    frappe.call({
        method: 'systech.services.api.get_salesperson_stats',
        args: {
            salesperson: frm.doc.name,
            start_date: frappe.datetime.month_start(),
            end_date: frappe.datetime.month_end()
        },
        no_cache: true,
        callback: function(r) {
            if (r.message) {
                const stats = r.message;
                frm.set_value('current_month_sales', stats.total_sales);
                frm.set_value('current_month_qty', stats.total_items);

                let is_target_met = false;
                let target_amt = stats.sales_target || 0;
                let target_qty = stats.target_qty || 0;

                if (target_amt > 0 && target_qty > 0) {
                     if (stats.total_sales >= target_amt && stats.total_items >= target_qty) is_target_met = true;
                } else if (target_amt > 0) {
                     if (stats.total_sales >= target_amt) is_target_met = true;
                } else if (target_qty > 0) {
                     if (stats.total_items >= target_qty) is_target_met = true;
                }

                let base_rate = frm.doc.commission_rate || 0;
                let incentive_rate = frm.doc.incentive_rate || 0;

                let status = "";
                let base_comm = (stats.total_sales * base_rate) / 100;
                let bonus_comm = 0;

                if (is_target_met) {
                    bonus_comm = (stats.total_sales * incentive_rate) / 100;
                    status = (incentive_rate > 0) ? `ðŸ”¥ Target Met! Earning ${base_rate}% + ${incentive_rate}% Incentive` : `Target Met! (Earning ${base_rate}% Commission)`;
                } else {
                    status = `Below Target (Standard: ${base_rate}%)`;
                    if (target_amt > 0) {
                        let remaining = target_amt - stats.total_sales;
                        if (remaining > 0) {
                            status += ` - Need ${format_currency(remaining, stats.currency)} more`;
                        }
                    }
                }

                frm.set_value('target_status', status);
                const total_comm = base_comm + bonus_comm;
                frm.set_value('estimated_commission', total_comm);

                // Visual feedback in the description
                let breakdown = `Standard (${base_rate}%): ${format_currency(base_comm, stats.currency)}`;
                if (bonus_comm > 0) {
                    breakdown += ` + Incentive (${incentive_rate}%): ${format_currency(bonus_comm, stats.currency)}`;
                }
                breakdown += ` = Total: ${format_currency(total_comm, stats.currency)}`;

                frm.set_df_property('target_status', 'description', breakdown);
            }
        }
    });
}

frappe.ui.form.on('Sales Invoice', {
    refresh: function (frm) {
        // Add custom Email button
        if (frm.doc.docstatus === 1 && frm.doc.customer) {
            frm.add_custom_button(__('Email'), function () {
                send_invoice_email(frm, 'customer');
            }, __('Actions'));
        }
    }
});

function send_invoice_email(frm, party_type) {
    let party_name = frm.doc.customer;

    if (!party_name) {
        frappe.msgprint(__('No customer selected'));
        return;
    }

    // Fetch email
    frappe.call({
        method: 'systech.api.email.get_party_email',
        args: {
            party_type: 'Customer',
            party_name: party_name
        },
        callback: function (r) {
            if (r.message) {
                // Email found, open dialog
                new frappe.views.CommunicationComposer({
                    doc: frm.doc,
                    frm: frm,
                    subject: __('{0}: {1}', [frm.doctype, frm.docname]),
                    recipients: r.message,
                    attach_document_print: true,
                    real_name: party_name
                });
            } else {
                // No email found
                frappe.msgprint({
                    title: __('Email Not Found'),
                    indicator: 'orange',
                    message: __(
                        'The Customer <b>{0}</b> does not have an email address.<br><br>' +
                        'Please <a href="/app/customer/{1}">add email to customer record</a> first.',
                        [party_name, party_name]
                    )
                });
            }
        }
    });
}
