script:
frappe.provide('salesperson_dashboard');

salesperson_dashboard = {
  root: null,
  data: null,

  // Initialize the dashboard
  init: function(root_element) {
    this.root = root_element;
    this.loadDashboardData();
    this.updateTimestamp();

    // Auto-refresh every 5 minutes
    setInterval(() => {
      this.loadDashboardData();
      this.updateTimestamp();
    }, 300000);
  },

  // Update the last updated timestamp
  updateTimestamp: function() {
    const now = new Date();
    const timeString = now.toLocaleString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      hour12: true
    });
    const updateEl = this.root.querySelector('#last-update-time');
    if (updateEl) {
      updateEl.textContent = timeString;
    }
  },

  // Load all dashboard data
  loadDashboardData: function() {
    frappe.call({
      method: 'systech.services.api.get_salesperson_dashboard_data',
      callback: (r) => {
        if (r.message) {
          this.data = r.message;
          this.updateDashboard(r.message);
        }
      },
      error: (r) => {
        console.error('Error loading salesperson dashboard data:', r);
        const dashboard = this.root.querySelector('.salesperson-dashboard');
        if (dashboard) {
          dashboard.innerHTML = 
            '<div class="text-center" style="padding: 60px; background: white; border-radius: 16px; margin: 24px;">' +
            '<h3 style="color: #dc2626; margin-bottom: 12px;">‚ö†Ô∏è Error Loading Dashboard</h3>' +
            '<p style="color: #6b7280;">Unable to load your sales data. Please try again or contact support.</p>' +
            '</div>';
        }
      }
    });
  },

  // Update dashboard with data
  updateDashboard: function(data) {
    const stats = data.stats;
    const salesperson = data.salesperson;
    const period = data.period;
    // Uses Item Group data but maps it to "Brand" section UI
    const categoryStock = data.category_stock || [];

    // Update salesperson name
    this.updateElement('#salesperson-name', salesperson || 'Not Assigned');

    // Update period
    const periodText = this.formatDate(period.start) + ' - ' + this.formatDate(period.end);
    this.updateElement('#current-period', periodText);

    // Update statistics
    this.updateElement('#stat-orders', stats.orders || 0);
    this.updateElement('#stat-invoices', stats.invoices || 0);
    this.updateElement('#stat-total-sales', this.formatCurrency(stats.total_sales, stats.currency));
    this.updateElement('#stat-total-items', this.formatNumber(stats.total_items));
    this.updateElement('#stat-sales-target', this.formatCurrency(stats.sales_target, stats.currency));
    this.updateElement('#stat-locked-items', stats.locked_items || 0);

    // Update progress bar
    this.updateProgressBar(stats.total_sales, stats.sales_target, stats.currency);

    // Update Brand Stock Section (using category data)
    this.updateBrandStock(categoryStock);
  },

  // Update Stock Logic
  updateBrandStock: function(items) {
    const container = this.root.querySelector('#brand-stock-container');
    if (!container) return;

    container.innerHTML = '';

    if (!items || items.length === 0) {
      container.innerHTML = '<div class="text-center" style="grid-column: 1/-1; padding: 20px; color: #718096;">No stock data found</div>';
      return;
    }

    items.forEach(item => {
      const card = document.createElement('div');
      card.className = 'brand-card';
      // Mapped group_name (Item Group) to display
      card.innerHTML = `
        <div class="brand-header">
          <h4 class="brand-name">${item.group_name || 'Uncategorized'}</h4>
          <span class="brand-items-count">${item.total_items} Items</span>
        </div>
        <div class="brand-stats">
          <div class="brand-stat-row">
            <span class="brand-stat-label">Stock Qty</span>
            <span class="brand-stat-value">${this.formatNumber(item.total_qty)}</span>
          </div>
          <div class="brand-stat-row">
            <span class="brand-stat-label">Stock Value</span>
            <span class="brand-stat-value value-highlight">${this.formatCurrency(item.total_value)}</span>
          </div>
        </div>
      `;
      container.appendChild(card);
    });
  },

  // Update progress bar
  updateProgressBar: function(current, target, currency) {
    const percentage = target > 0 ? Math.min((current / target) * 100, 100) : 0;

    const progressBar = this.root.querySelector('#progress-bar');
    const progressPercentage = this.root.querySelector('#progress-percentage');
    const targetAchievement = this.root.querySelector('#target-achievement');
    const progressCurrent = this.root.querySelector('#progress-current');
    const progressTarget = this.root.querySelector('#progress-target');

    if (progressBar) {
      progressBar.style.width = percentage + '%';
    }

    if (progressPercentage) {
      progressPercentage.textContent = percentage.toFixed(1) + '%';
    }

    if (targetAchievement) {
      targetAchievement.textContent = percentage.toFixed(1);
    }

    if (progressCurrent) {
      progressCurrent.textContent = 'Current: ' + this.formatCurrency(current, currency);
    }

    if (progressTarget) {
      progressTarget.textContent = 'Target: ' + this.formatCurrency(target, currency);
    }
  },

  // Helper to update element text
  updateElement: function(selector, text) {
    const el = this.root.querySelector(selector);
    if (el) {
      el.textContent = text;
    }
  },

  // Helper function to format currency
  formatCurrency: function(amount, currency) {
    if (amount == null || amount === undefined) amount = 0;
    currency = currency || frappe.boot.sysdefaults.currency || 'USD';
    return format_currency(amount, currency);
  },

  // Helper function to format number
  formatNumber: function(num) {
    if (num == null || num === undefined) return '0';
    return parseFloat(num).toLocaleString('en-US', {
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    });
  },

  // Helper function to format date
  formatDate: function(dateStr) {
    if (!dateStr) return '-';
    return frappe.datetime.str_to_user(dateStr);
  },

  // Refresh data
  refresh: function() {
    this.loadDashboardData();
    this.updateTimestamp();
  }
};

// Attach refresh function to window for button onclick handler
window.refreshSalespersonDashboard = function() {
  if (salesperson_dashboard.root) {
    salesperson_dashboard.refresh();
  }
};

// Initialize dashboard using root_element
salesperson_dashboard.init(root_element);

