  --card-color-2: #7c3aed;
}

.stat-card-green {
  --card-color-1: #10b981;
  --card-color-2: #059669;
}

.stat-card-orange {
  --card-color-1: #f59e0b;
  --card-color-2: #d97706;
}

.stat-card-teal {
  --card-color-1: #14b8a6;
  --card-color-2: #0d9488;
}

.stat-card-red {
  --card-color-1: #ef4444;
  --card-color-2: #dc2626;
}

.stat-icon {
  font-size: 40px;
  line-height: 1;
