  justify-content: space-between;
  font-size: 14px;
  color: #4a5568;
  font-weight: 500;
}

/* Dashboard Footer */
.dashboard-footer {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background: white;
  border-radius: 16px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.05);
