}

.brand-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 16px;
}

.brand-name {
  font-size: 16px;
  font-weight: 700;
  color: #000000;
  margin: 0;
}

.brand-items-count {
  font-size: 12px;
  color: #4a5568;
  background: #f7fafc;
  padding: 4px 8px;
  border-radius: 6px;
  font-weight: 600;
}

.brand-stats {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.brand-stat-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
