}

/* Responsive Design */
@media (max-width: 768px) {
  .salesperson-dashboard {
    padding: 16px;
  }

  .dashboard-header {
    flex-direction: column;
    align-items: flex-start;
    gap: 16px;
  }

  .dashboard-title {
    font-size: 24px;
  }

  .stats-grid {
    grid-template-columns: 1fr;
    gap: 16px;
  }

  .dashboard-footer {
    flex-direction: column;
    gap: 16px;
  }

  .refresh-btn {
    width: 100%;
    justify-content: center;
  }
}

script:
// 1. Capture the local root_element immediately
(function(root) {
    if (!root) return;

    // 2. Define the update function
    const refreshData = () => {
        // Target our internal container for the loading effect
        const container = root.querySelector('#sales-dashboard-container');
        if (container) container.style.opacity = '0.5';

        frappe.call({
            method: "systech.services.api.get_salesperson_dashboard_data",
            callback: (r) => {
                if (container) container.style.opacity = '1';
                if (!r.message) return;

                const { stats, salesperson, locked_items_details } = r.message;
                const items = locked_items_details || [];

                // Helper to update text safely inside this root
                const set = (sel, val) => {
                    const el = root.querySelector(sel);
                    if (el) el.innerText = val;
                };

                // Update Header & Stats
                set('#sp-name', `Welcome, ${salesperson || 'Salesperson'}`);
                set('#stat-orders', stats.orders || 0);
                set('#stat-locked-qty', stats.locked_items || 0);
                set('#stat-locked-count', `${items.length} Locked Orders`);

                // Commission (10% + 7% logic)
                const isMet = stats.total_sales >= stats.sales_target;
                const rate = isMet ? 17 : 10;
                const totalComm = (stats.total_sales * rate) / 100;
                set('#stat-commission', format_currency(totalComm, stats.currency));

                // Update Badge
                const badge = root.querySelector('#target-badge');
                if (badge) {
                    badge.innerText = isMet ? `üî• Bonus Unlocked (${rate}%)` : `Standard Rate (${rate}%)`;
                    badge.className = isMet ? 'badge target-met' : 'badge';
                }

                // Update Table
                const tbody = root.querySelector('#locked-items-body');
                if (tbody) {
                    tbody.innerHTML = items.length ? items.map(i => `
                        <tr>
                            <td><strong>${i.name}</strong></td>
                            <td>${i.customer}</td>
                            <td>${i.qty} units</td>
                            <td>${frappe.datetime.global_date_format(i.date)}</td>
                        </tr>
                    `).join('') : '<tr><td colspan="4" class="empty-state">No locked items found.</td></tr>';
                }
            }
        });
    };

    // 3. Bind the refresh button using the root reference
    // We use a small timeout to ensure the HTML is rendered before we search for the button
    setTimeout(() => {
        const btn = root.querySelector('.refresh-btn');
        if (btn) {
            btn.onclick = () => refreshData();
        }
        refreshData(); // Run initial load
    }, 50);

})(root_element);

